import requests

url = "https://api.travelpayouts.com/v2/prices/month-matrix"

querystring = {"currency":"usd","show_to_affiliates":"true","origin":"NYC","destination":"LAX"}

headers = {'x-access-token': '2160a9f9ca2fa3d348f4a3a32504538e'}

response = requests.request("GET", url, headers=headers, params=querystring)

# print(type(response.json()))

# Customer wants to fly from NYC to LAX, departing on the date October 9th. Customer wants to see all available options on that departure date for those flights
# Customer also wants to know the duration of those flights as well.

# Access list in dict value, we need to get departure date and duration. 
dictionary = response.json()
dict_value = dictionary["data"]
flight_duration = []

# check if departure date is 2024-10-09, if true then grab duration, put into list.
for item in dict_value:
    if item["depart_date"] == "2024-10-09":
        print(f"There is a flight from NYC to LAX with a duration of {item["duration"]}")


